import React, { Component } from "react";
import queryString from "query-string";
import PropTypes from "prop-types";
import Searchbar from "../components/Searchbar";
import * as articleAPI from "../services/articles-api";

export default class Movies extends Component {
  static propTypes = {
    history: PropTypes.shape({
      push: PropTypes.func.isRequired,
    }).isRequired,
    location: PropTypes.shape({
      search: PropTypes.string.isRequired,
    }).isRequired,
    match: PropTypes.shape({
      params: PropTypes.shape({
        id: PropTypes.string,
      }),
    }).isRequired,
  };

  state = {
    input: "",
    data: [],
    // notFound: false,
  };

  // componentDidMount() {
  //   const { location } = this.props;
  //   const { query } = queryString.parse(location.search);
  //   if (query) {
  //     articleAPI
  //       .getMoviesQuery(query)
  //       .then();
  //     // .catch((error) => this.apiError(error));
  //   }
  // }

  componentDidUpdate(prevProps) {
    const { location } = this.props;
    const { query } = queryString.parse(location.search);

    if (prevProps.location.search !== location.search) {
      articleAPI.getMoviesQuery(query).then((data) => console.log(data));
      // .catch((error) => this.apiError(error));
    }
  }

  handleOnSubmit = (e) => {
    // e.preventDefault();
    this.setState({ data: data.results, query: e });
  };

  render() {
    return (
      <>
        <Searchbar onSubmit={this.handleOnSubmit} />
      </>
    );
  }
}
