import React from "react";

const Movie = ({ title, poster_path, backdrop_path, overview, onGoback }) => (
  <article>
    <h2>{title}</h2>
    <img src={backdrop_path} alt={title} />

    <p>{overview}</p>
    <button onClick={onGoback} type="button">
      Back to articles
    </button>
  </article>
);

export default Movie;
