import React from "react";
import Searchbar from "../components/Searchbar";
import * as articleAPI from "../services/articles-api";

export default class Movies extends Component {
  state = { allData: [], page: 0, query: "" };

  componentDidUpdate(prevProps, prevState) {
    const { query } = this.state;
    if (query !== "") {
      this.getDataByParams({ query });
    }
  }

  getDataByParams = ({ query }) => {
    const { allData } = this.state;

    return (
      getMoviesQuery({ query, page })
        .then(({ data }) =>
          this.setState({ allData: [...allData, ...data.hits] })
        )
        // eslint-disable-next-line no-alert
        .catch((error) => alert(error))
        .finally(() => {
          this.setState({ isLoading: false });
          window.scrollTo({
            top: scrollHeight,
            behavior: "smooth",
          });
        })
    );
  };

  handleOnSubmit = (e) => {
    this.setState({ allData: [], query: e, page: 1 });
  };

  render() {
    return (
      <>
        <Searchbar onSubmit={this.handleOnSubmit} />
      </>
    );
  }
}
