import React from 'react';
import Select from 'react-select';

const styles = {
  select: {
    fontSize: 20,
  },
};

const CategorySelector = ({ options, value, onChange }) => (
  <Select options={options} isClearable value={value} onChange={onChange} />
);

export default CategorySelector;
