import ArticleList from "../components/ArticleList";
import * as articleAPI from "../services/articles-api";
// import ArticlePage from './ArticlePage';

const categories = [
  {
    value: "health",
    label: "Health",
  },
  {
    value: "technology",
    label: "Technology",
  },
  {
    value: "sports",
    label: "Sports",
  },
];

