import React from "react";
import { NavLink } from "react-router-dom";

const activeStyle = {
  color: "palevioletred",
};

const Nav = () => (
  <nav>
    <ul className={styles.list}>
      <li>
        <NavLink to="/" exact activeClassName={styles.link}>
          Home
        </NavLink>
      </li>
      <li>
        <NavLink to="/movies" activeClassName={styles.link}>
          Movies
        </NavLink>
      </li>
    </ul>
  </nav>
);
export default Nav;
