import axios from "axios";

const API_BASE_URL = "https://api.themoviedb.org/3/";
const API_KEY = "16dd81a9c48e022ac37ab1a286d37f0a";

export const getTrendingMovies = () => {
  return axios.get(
    "https://api.themoviedb.org/3/trending/movie/day?api_key=16dd81a9c48e022ac37ab1a286d37f0a"
  );
};

export const fetchArticles = (category = null) => {
  const url = category ? `/articles?category=${category}` : "/articles";

  return axios.get(url).then((response) => response.data);
};

export const fetchMovieWithId = (id) => {
  return axios.get(`${API_BASE_URL}movie/${id}?api_key=${API_KEY}`);
};
