import React, { Component } from "react";
import queryString from "query-string";
import PropTypes from "prop-types";
import SearchBar from "../components/SearchBar";
import * as articleAPI from "../services/articles-api";

export default class Movies extends Component {
  static propTypes = {
    history: PropTypes.shape({
      push: PropTypes.func.isRequired,
    }).isRequired,
    location: PropTypes.shape({
      search: PropTypes.string.isRequired,
    }).isRequired,
    match: PropTypes.shape({
      params: PropTypes.shape({
        id: PropTypes.string,
      }),
    }).isRequired,
  };

  state = {
    query: "",
    movies: [],
    // notFound: false,
  };

  componentDidMount() {
    const pared = queryString.parse(this.props.location.search).query;
    if (parsed) {
      if (parsed !== undefined) {
        articleAPI
          .getMoviesQuery(parsed)
          .then(({ data }) => this.setState({ movies: data.results }));
      }
    }
  }

  componentDidUpdate(prevProps) {
    const { location } = this.props;
    const { query } = queryString.parse(location.search);

    if (prevProps.location.search !== location.search) {
      articleAPI
        .getMoviesQuery(query)
        .then(({ data }) => this.setState({ data: data.results }));
      // .catch((error) => this.apiError(error));
    }
  }

  handleOnSubmit = (e) => {
    // e.preventDefault();
    this.setState({ data: [], query: e });
  };

  render() {
    return (
      <>
        <SearchBar onSubmit={this.handleOnSubmit} />
      </>
    );
  }
}
