import React from "react";
import { NavLink } from "react-router-dom";

const activeStyle = {
  color: "palevioletred",
};

const Nav = () => (
  <ul>
    <li>
      <NavLink to="/" exact activeStyle={activeStyle}>
        Home
      </NavLink>
    </li>
    <li>
      <NavLink to="/about" activeStyle={activeStyle}>
        Movies
      </NavLink>
    </li>
  </ul>
);

export default Nav;
