import React, { Component } from "react";
import queryString from "query-string";
import PropTypes from "prop-types";
import SearchBar from "../components/SearchBar";
import * as articleAPI from "../services/articles-api";

export default class Movies extends Component {
  static propTypes = {
    history: PropTypes.shape({
      push: PropTypes.func.isRequired,
    }).isRequired,
    location: PropTypes.shape({
      search: PropTypes.string.isRequired,
    }).isRequired,
    match: PropTypes.shape({
      params: PropTypes.shape({
        id: PropTypes.string,
      }),
    }).isRequired,
  };

  state = {
    query: "",
    movies: [],
    // notFound: false,
  };

  componentDidMount() {
    const parsed = queryString.parse(this.props.location.search).query;
    if (parsed) {
      if (parsed !== undefined) {
        articleAPI.getTrendingMovies(parsed).then(({ data }) => {
          this.setState({ movies: data.results });
        });
      }
    }
  }

  onChange = (value) => {
    this.setState({ query: value });
  };

  onSearch = (query) => {
    this.props.history.push({
      ...this.props.location,
      search: `query=${query}`,
    });
    articleAPI.getMoviesQuery(query).then(({ data }) => {
      this.setState({ movies: data.results });
    });
  };

  render() {
    return (
      <>
        <SearchBar onSubmit={this.handleOnSubmit} />
      </>
    );
  }
}
